// sendEmail.js

import emailjs from "emailjs-com";
import { SendemailContexte } from "./contexte/SendemailContext";

const serviceId = "service_grgniwv";
const templateId = "template_53g2tgc";
const userId = "SAIRVAjiLd1aXOQyd";
const accessToken = 'o2bkvVsf00j72LHu7Eerv';

// const {objet, 
//   setObjet, 
//   firstname,
//   setFirstname, 
//   lastname,
//   setLastname, 
//   password,
//   setPassword,
//   email, setEmail, message, setMessage, 
//   city, setCity, state, setState, zip, setZip
// } = useContext(SendemailContexte);

const sendEmail = async (objet, firstname, lastname, email, password, city, state, zip, message) => {
  try {
    const response = await emailjs.send(
      serviceId,
      templateId,
      { objet, firstname, lastname, email, password, message, city, state, zip },
      userId,
      accessToken,
    );

    if (response.status === 200) {
      console.log("Successfully sent message.");
    }
  } catch (error) {
    console.error("Failed to send email. Error: ", error);
  }
};

export default sendEmail;
