import React from 'react';
import Image2 from '../images/img2_5.jpg';
import ImgHtmlCss from '../images/reactjs2.svg';
import Zoom from 'react-reveal/Zoom';


const LesSites = [
  {
    id: 1,
    nom: 'Fake Store API',
    image: ImgHtmlCss,
    categorie: 'E-commerce',
    technologies: 'React.js, Redux, CSS et Bootstrap',
  },
  {
    id: 2,
    nom: 'Serie de Breaking Bad',
    image: ImgHtmlCss,
    categorie: 'Vitrine',
    technologies: 'React.js, Context-API et Tailwind-CSS',
  },
  {
    id: 3,
    nom: 'Argan et Amlo',
    image: ImgHtmlCss,
    categorie: 'Vitrine',
    technologies: 'HTML et CSS',
  },
  {
    id: 3,
    nom: 'Portfolio, Envoyer un message',
    image: ImgHtmlCss,
    categorie: 'Vitrine',
    technologies: 'React.js, context-API, EmailJs et Tailwind-CSS',
  }
]


const Projects = () => {
  return (
    <div id="projets" style={{backgroundImage: `url(${Image2})`}} className="w-full h-full bg-cover bg-no-repeat bg-center">
      <div className='flex flex-col items-center p-8 h-full w-full mx-auto text-center text-white bg-black bg-opacity-75'>
          <h1 className='flex-none mb-6'>Les Projets</h1>
          <div className='flex-1 grid gap-4 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 grid-rows-2 items-center'>
            {LesSites.map((site) => {
              const {id, nom, image, categorie, technologies} = site;
              return (
                <Zoom>
                      <div key={id} className="card card-side w-98 mx-4 bg-base-300 text-primary shadow-xl">
                        <figure><img src={image} alt="Shoes" className="rounded-xl ml-6" /></figure>
                        <div className="card-body items-center text-center">
                          <h2 className="card-title">{nom}</h2>
                          <h5 className="card-title">{categorie}</h5>
                          <p>{technologies}</p> 
                          <div className="card-actions">
                            <button className="btn btn-primary">Buy Now</button>
                          </div>
                        </div>
                      </div>
                  </Zoom>
                      )
            })}
          </div>
      </div>
    </div>
  )
}

export default Projects
