import React from 'react';
import ImgReact from '../images/reactjs2.svg';
import ImgHtmlCssJs from '../images/html_css_js.svg';
import ImgCss3 from '../images/css3.svg';

const LesTechnologies = [
  {
    id: 1,
    nom: 'React.js',
    image: ImgReact,
    technologies: 'React.js, React Hooks, Redux et Context-API',
  },
  {
    id: 2,
    nom: 'HTML, CSS et JS',
    image: ImgHtmlCssJs,
    technologies: 'HTML, CSS et JavaScript',
  },
  {
    id: 3,
    nom: 'librairies CSS',
    image: ImgCss3,
    technologies: 'Tailwind-CSS, Bootstrap et SASS',
  }, 
]

const Skills = () => {
  return (
    <div id='skills' className='bg-gradient-to-b from-black via-[#040008] to-[#0e1217] h-[80vh] text-center flex items-center justify-center gap-10 p-12'>
      {LesTechnologies.map((technologie) => {
        const {id, nom, image, technologies} = technologie;
        return (
          <div className="card w-96 bg-transparent text-[#afafaf] shadow-md shadow-[#afafaf]">
              <figure className="px-10 pt-10">
                  <img src={image} alt="image" className="rounded-xl ml-6 h-22 w-28" />
              </figure>
              <div className="card-body items-center text-center">
                  <h2 className="card-title">{nom}</h2>
                  <p>{technologies}</p>
              </div>
          </div>
        )
      })}
    </div>
  )
}

export default Skills
