import React from 'react';
import Image2 from '../images/img2_5.jpg';

const LesSites = [
  {
    id: 1,
    nom: 'Fake Store API',
    categorie: 'E-commerce',
    technologies: 'React.js, Redux, CSS et Bootstrap',
  },
  {
    id: 2,
    nom: 'Serie de Breaking Bad',
    categorie: 'Vitrine',
    technologies: 'React.js, Context-API et Tailwind-CSS',
  },
  {
    id: 3,
    nom: 'Argan et Amlo',
    categorie: 'Vitrine',
    technologies: 'HTML et CSS',
  },
  {
    id: 3,
    nom: 'Portfolio',
    categorie: 'Vitrine',
    technologies: 'React.js, context-API, EmailJs et Tailwind-CSS',
  }
]


const Projects = () => {
  return (
    <div id="projets" style={{backgroundImage: `url(${Image2})`}} className="w-full h-screen bg-cover bg-no-repeat bg-center">
      <div className='flex flex-col items-center p-8 h-full w-full mx-auto text-center text-white bg-black bg-opacity-75'>
          <h1 className='flex-none mb-6'>Les Projets</h1>
          <div className='flex-1 grid gap-4 grid-cols-1 sm:grid-cols-3 grid-rows-2 items-center'>
            {LesSites.map((site) => {
              const {id, nom, categorie, technologies} = site;
              return (
                      <div key={id} className="card w-96 bg-neutral text-neutral-content">
                        <div className="card-body items-center text-center">
                          <h2 className="card-title">{nom}</h2>
                          <h4>{categorie}</h4>
                          <p>Les Technologies: <br/> {technologies}</p>
                          <div className="card-actions justify-end mt-3">
                            <button className="btn btn-primary">Lien</button>
                            <button className="btn btn-ghost">GitHub</button>
                          </div>
                        </div>
                      </div>
                      )
            })}
          </div>
      </div>
    </div>
  )
}

export default Projects
