import React from 'react'
import Image from '../images/img5.jpg'

const Home = () => {
  const [placeholder, setPlaceholder] = React.useState('');

  const
    string = 'Hello My Name Is Younes EL MOUATASIM',
    index = React.useRef(0);

  React.useEffect(() => {
    function tick() {
      setPlaceholder(prev => prev + string[index.current]);
      index.current++;
    }
    if (index.current < string.length) {
      let addChar = setInterval(tick, 500);
      return () => clearInterval(addChar);
    }
  }, [placeholder]);
  return (
    <div style={{backgroundImage: `url(${Image})`}} className="relative w-full h-screen bg-cover bg-no-repeat bg-center" >
        <div className='absolute h-full w-full text-white bg-gradient-to-b from-black'>
          {placeholder}
          {'Hiya how are you'.substring(0,50)}
        </div>
    </div>
  )
}

export default Home
